/**
 * Categories HTTP client.
 *
 * This module implements the categories related HTTP requests.
 */
App.Http.Categories = function () {
  /**
   * Save (create or update) a category.
   *
   * @param {Object} category
   *
   * @return {Object}
   */
  function save(category) {
    return category.id ? update(category) : create(category);
  }
  /**
   * Create a category.
   *
   * @param {Object} category
   *
   * @return {Object}
   */


  function create(category) {
    var url = App.Utils.Url.siteUrl('categories/create');
    var data = {
      csrf_token: vars('csrf_token'),
      category: category
    };
    return $.post(url, data);
  }
  /**
   * Update a category.
   *
   * @param {Object} category
   *
   * @return {Object}
   */


  function update(category) {
    var url = App.Utils.Url.siteUrl('categories/update');
    var data = {
      csrf_token: vars('csrf_token'),
      category: category
    };
    return $.post(url, data);
  }
  /**
   * Delete a category.
   *
   * @param {Number} categoryId
   *
   * @return {Object}
   */


  function destroy(categoryId) {
    var url = App.Utils.Url.siteUrl('categories/destroy');
    var data = {
      csrf_token: vars('csrf_token'),
      category_id: categoryId
    };
    return $.post(url, data);
  }
  /**
   * Search categories by keyword.
   *
   * @param {String} keyword
   * @param {Number} limit
   * @param {Number} offset
   * @param {String} orderBy
   *
   * @return {Object}
   */


  function search(keyword, limit, offset, orderBy) {
    var url = App.Utils.Url.siteUrl('categories/search');
    var data = {
      csrf_token: vars('csrf_token'),
      keyword: keyword,
      limit: limit,
      offset: offset,
      order_by: orderBy
    };
    return $.post(url, data);
  }
  /**
   * Find a category.
   *
   * @param {Number} categoryId
   *
   * @return {Object}
   */


  function find(categoryId) {
    var url = App.Utils.Url.siteUrl('categories/find');
    var data = {
      csrf_token: vars('csrf_token'),
      category_id: categoryId
    };
    return $.post(url, data);
  }

  return {
    save: save,
    create: create,
    update: update,
    destroy: destroy,
    search: search,
    find: find
  };
}();