/**
 * Providers HTTP client.
 *
 * This module implements the providers related HTTP requests.
 */
App.Http.Providers = function () {
  /**
   * Save (create or update) a provider.
   *
   * @param {Object} provider
   *
   * @return {Object}
   */
  function save(provider) {
    return provider.id ? update(provider) : create(provider);
  }
  /**
   * Create a provider.
   *
   * @param {Object} provider
   *
   * @return {Object}
   */


  function create(provider) {
    var url = App.Utils.Url.siteUrl('providers/create');
    var data = {
      csrf_token: vars('csrf_token'),
      provider: provider
    };
    return $.post(url, data);
  }
  /**
   * Update a provider.
   *
   * @param {Object} provider
   *
   * @return {Object}
   */


  function update(provider) {
    var url = App.Utils.Url.siteUrl('providers/update');
    var data = {
      csrf_token: vars('csrf_token'),
      provider: provider
    };
    return $.post(url, data);
  }
  /**
   * Delete a provider.
   *
   * @param {Number} providerId
   *
   * @return {Object}
   */


  function destroy(providerId) {
    var url = App.Utils.Url.siteUrl('providers/destroy');
    var data = {
      csrf_token: vars('csrf_token'),
      provider_id: providerId
    };
    return $.post(url, data);
  }
  /**
   * Search providers by keyword.
   *
   * @param {String} keyword
   * @param {Number} limit
   * @param {Number} offset
   * @param {String} orderBy
   *
   * @return {Object}
   */


  function search(keyword, limit, offset, orderBy) {
    var url = App.Utils.Url.siteUrl('providers/search');
    var data = {
      csrf_token: vars('csrf_token'),
      keyword: keyword,
      limit: limit,
      offset: offset,
      order_by: orderBy
    };
    return $.post(url, data);
  }
  /**
   * Find a provider.
   *
   * @param {Number} providerId
   *
   * @return {Object}
   */


  function find(providerId) {
    var url = App.Utils.Url.siteUrl('providers/find');
    var data = {
      csrf_token: vars('csrf_token'),
      provider_id: providerId
    };
    return $.post(url, data);
  }

  return {
    save: save,
    create: create,
    update: update,
    destroy: destroy,
    search: search,
    find: find
  };
}();